"use strict";

function int(str, undef) {
  var num = parseInt(str, 10);

  if (isNaN(num)) {
    if (undef === true) {
      return;
    } else {
      return str;
    }
  }

  return num;
}

function repeat(str, times) {
  times = times || 1;
  var ret = '';

  for (var i = 0; i < times; i++) {
    ret += str;
  }

  return ret;
}

module.exports = {
  int: int,
  repeat: repeat
};